mapper.update:
UPDATE tb_article
SET 
	title = #{title},
	contents = #{contents}
	-- reg_date ? // XXX 날짜를 수정해도 글 목록의 순서는 그대로?? // TODO 필요필드 : 작성일, 작성자, 수정일, 수정자
WHERE article_id = #{articleId}

mapper.insert:
INSERT INTO TB_ARTICLE_READ_COUNT
(
		ARTICLE_ID
	,	USER_ID
)
VALUES
(
		#{articleId}
	,	#{userId}
)

insert.foreach:
INTO TB_ARTICLE_RANK
(
		ARTICLE_ID
	,	COMMENT_COUNT_RANK
	, 	READ_COUNT_RANK
	,	POPULARITY_RANK
	,	BEST_RANK
	,	RISING_RANK
)
VALUES
(
		#{item.articleId}
	,	#{item.commentCountRank}
	,	#{item.readCountRank}
	,	#{item.popularityRank}
	,	#{item.bestRank}
	,	#{item.risingRank}
)
